<style>
.loading {
    display: flex;
    justify-content: center;
    align-items: center;
    height: 100vh;
    opacity: 0;
    animation: fade-in 1s forwards; 
}

.ball {
    position: relative;
    bottom: 50px;
    left: calc(100% - 20px);
    width: 50px;
    height: 50px;
    border-radius: 50%;
    animation: ball-move8234 3s ease-in-out 1s infinite alternate;
  }

.bar {
    width: 200px;
    height: 12.5px;
    background: grey;
    border-radius: 30px;
    transform: rotate(-15deg);
    animation: up-down6123 3s ease-in-out 1s infinite alternate;
}

.svg {
  fill : orange;
  position: relative;
  top: -0px;
  left: -0px;
  width: 50px;
  height: 50px;
  animation: rotate 3s ease-in-out 1s infinite alternate;
}

:global(body.dark-mode) .loading {
  color: var(--white-color);
}

:global(body.dark-mode) .ball {
  color: var(--white-color);
}

:global(body.dark-mode) .bar {
  background-color: var(--grey-color);
  color : var(--white-color);
}

:global(body.dark-mode) .svg {
  color: var(--white-color);
}

@keyframes up-down6123 {
  from {
    transform: rotate(-15deg);
  }

  to {
    transform: rotate(15deg);
  }
}

@keyframes ball-move8234 {
  from {
    left: calc(100% - 40px);
    transform: rotate(360deg);
  }

  to {
    left: calc(0% - 20px);
    transform: rotate(0deg);
  }
}

@keyframes fade-in {
    to {
      opacity: 1;
    }
}

</style>